PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# Source: https://list.indiana.edu/sympa/arc/mla-l/2017-08/msg00248.html
# [en] Could I find any music for flute and TWO bassoons ? an interesting combo

SELECT DISTINCT ?expression, SAMPLE(?composer) as ?composer, SAMPLE(?title) as ?title, ?casting
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting .

  ?expCreation efrbroo:R17_created ?expression ;
          ecrm:P9_consists_of / ecrm:P14_carried_out_by / foaf:name ?composer .

  ?casting mus:U23_has_casting_detail ?castingDet1, ?castingDet2 .
  ?castingDet1 mus:U2_foresees_use_of_medium_of_performance / skos:exactMatch* ?bassoon ;
       mus:U30_foresees_quantity_of_mop 2 .

  ?castingDet2 mus:U2_foresees_use_of_medium_of_performance / skos:exactMatch* ?flute .

  VALUES ?bassoon { <http://www.mimo-db.eu/InstrumentsKeywords/3795> }
  VALUES ?flute { <http://www.mimo-db.eu/InstrumentsKeywords/3955> }
}
GROUP BY ?expression ?casting

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# Source: https://list.indiana.edu/sympa/arc/mla-l/2017-08/msg00248.html
# [en] Could I find any music for flute and TWO bassoons (strictly)? an interesting combo

SELECT DISTINCT ?expression, SAMPLE(?composer) as ?composer, SAMPLE(?title) as ?title, ?casting
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting .

  ?expCreation efrbroo:R17_created ?expression ;
          ecrm:P9_consists_of / ecrm:P14_carried_out_by / foaf:name ?composer .

  ?casting mus:U23_has_casting_detail ?allCastingDets .

  ?casting mus:U23_has_casting_detail ?castingDet1, ?castingDet2 .
  ?castingDet1 mus:U2_foresees_use_of_medium_of_performance / skos:exactMatch* ?bassoon ;
       mus:U30_foresees_quantity_of_mop 2 .

  ?castingDet2 mus:U2_foresees_use_of_medium_of_performance / skos:exactMatch* ?flute .

  VALUES ?bassoon { <http://www.mimo-db.eu/InstrumentsKeywords/3795> }
  VALUES ?flute { <http://www.mimo-db.eu/InstrumentsKeywords/3955> }
}
GROUP BY ?expression ?casting
HAVING (COUNT(?allCastingDets) = 2)

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# [en] Which works have been written for string quartet?
# [fr] Quelles oeuvres ont été écrites pour un quatuor à cordes ?


SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, ?casting
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting .

    ?casting mus:U23_has_casting_detail ?allCastingDets .

    ?casting mus:U23_has_casting_detail ?castingDet1 .
    ?castingDet1 mus:U2_foresees_use_of_medium_of_performance ?violin ;
            mus:U30_foresees_quantity_of_mop 2 .


    ?casting mus:U23_has_casting_detail ?castingDet2 .
    ?castingDet2 mus:U2_foresees_use_of_medium_of_performance ?viola ;
            mus:U30_foresees_quantity_of_mop 1 .

    ?casting mus:U23_has_casting_detail ?castingDet3 .
    ?castingDet3 mus:U2_foresees_use_of_medium_of_performance ?cello ;
            mus:U30_foresees_quantity_of_mop 1 .

  VALUES (?violin) { ( <http://data.doremus.org/vocabulary/iaml/mop/svl> ) (<http://www.mimo-db.eu/InstrumentsKeywords/3573>) }
  VALUES (?viola) { (<http://data.doremus.org/vocabulary/iaml/mop/sva>) (<http://www.mimo-db.eu/InstrumentsKeywords/3561>) }
  VALUES (?cello) { (<http://data.doremus.org/vocabulary/iaml/mop/svc>) (<http://www.mimo-db.eu/InstrumentsKeywords/3582>) }

}
GROUP BY ?expression ?casting
HAVING (COUNT(?allCastingDets) = 3)

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# [en] Give me all the works that have been written by German composers between 1800 and 1850 and performed at the Royal Albert Hall.
# [fr] donne-moi toutes les oeuvres de compositeurs allemands écrites entre 1800 et 1850 et exécutées au Royal Albert Hall


SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, ?start as ?date, SAMPLE(?composerName) as ?composer
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title .

  ?expCreation efrbroo:R17_created ?expression ;
          ecrm:P4_has_time-span ?ts ;
          ecrm:P9_consists_of [ ecrm:P14_carried_out_by ?composer  ;
                                 mus:U31_had_function <http://data.doremus.org/vocabulary/function/composer>
                               ] .

  ?composer foaf:name ?composerName ; schema:birthPlace / geonames:countryCode "DE".

  ?ts time:hasEnd / time:inXSDDate ?end ;
                time:hasBeginning / time:inXSDDate ?start .

  ?performance a efrbroo:F31_Performance ;
    efrbroo:R25_performed / ecrm:P165_incorporates ?expression ;
    ecrm:P7_took_place_at	<http://sws.geonames.org/6944344/> .

  FILTER ( ?start >= "1800"^^xsd:gYear AND ?end <= "1850"^^xsd:gYear )
}
ORDER BY ?start
LIMIT 500

# Note: we have not the information about the composer nationality. We use the birth place

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# [en] Give me the flute sonatas that last less than or equal to 15 minutes
# [fr] Donne-moi les sonates pour flûte d’une durée inférieure ou égale à 15 minutes

SELECT DISTINCT ?duration , SAMPLE(?title) as ?title , ?expression
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting / mus:U23_has_casting_detail / mus:U2_foresees_use_of_medium_of_performance / skos:exactMatch* ?flute ;
          mus:U12_has_genre / skos:exactMatch* <http://data.doremus.org/vocabulary/iaml/genre/sn> ;
          mus:U78_estimated_duration ?duration .


  VALUES ?flute { <http://www.mimo-db.eu/InstrumentsKeywords/3955> }

  FILTER (?duration <= 900)
}

# No records found

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# [en] Give me all the sonatas for piano and violin whose duration is between 20 and 30 minutes
# [fr] Donne-moi toutes les sonates pour piano et violon dont la durée est comprise entre 20 et 30 minutes

SELECT DISTINCT ?duration , SAMPLE(?title) as ?title , ?expression
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting / mus:U23_has_casting_detail / mus:U2_foresees_use_of_medium_of_performance / skos:exactMatch* ?piano, ?violin ;
          mus:U12_has_genre / skos:exactMatch* <http://data.doremus.org/vocabulary/iaml/genre/sn> ;
          mus:U78_estimated_duration ?duration .


  VALUES ?violin { <http://www.mimo-db.eu/InstrumentsKeywords/3573>}
  VALUES ?piano { <http://www.mimo-db.eu/InstrumentsKeywords/2299>}

  FILTER (?duration >= 1200 AND ?duration <= 1800)
}

# No records found

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# [en] Give me the works written for oboe and orchestra after the 1900
# [fr] Donne-moi les oeuvres écrites pour hautbois et orchestre depuis 1900


SELECT DISTINCT ?start, ?expression, SAMPLE(?title) as ?title, ?casting
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting .

  VALUES (?oboe) { ( <http://data.doremus.org/vocabulary/iaml/mop/wob> ) ( <http://www.mimo-db.eu/InstrumentsKeywords/4164> ) }
  VALUES (?orchestra) { ( <http://data.doremus.org/vocabulary/iaml/mop/o> ) }


  ?casting mus:U23_has_casting_detail / mus:U2_foresees_use_of_medium_of_performance ?oboe .

  { ?casting mus:U23_has_casting_detail / mus:U2_foresees_use_of_medium_of_performance ?orchestra }
  UNION
  { ?casting mus:U23_has_casting_detail / mus:U2_foresees_use_of_medium_of_performance / skos:broader ?orchestra  }

  ?expCreation efrbroo:R17_created ?expression ;
          ecrm:P4_has_time-span / time:hasBeginning / time:inXSDDate ?start .

  FILTER ( ?start > "1900"^^xsd:gYear )
} ORDER BY ?start

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# [en] Give me the works of chamber music that involves at least violin, clarinet and piano
# [fr] Donne-moi les oeuvres de musique de chambre comportant au moins violon, clarinette et piano



SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, ?genre, ?casting
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting ;
          mus:U12_has_genre / skos:prefLabel ?genre.

  ?casting mus:U23_has_casting_detail ?castingDet1 .
  ?castingDet1 mus:U2_foresees_use_of_medium_of_performance ?violin .

  ?casting mus:U23_has_casting_detail ?castingDet2 .
  ?castingDet2 mus:U2_foresees_use_of_medium_of_performance ?clarinet.

  ?casting mus:U23_has_casting_detail ?castingDet3 .
  ?castingDet3 mus:U2_foresees_use_of_medium_of_performance  ?piano .

  VALUES (?violin) { ( <http://data.doremus.org/vocabulary/iaml/mop/svl> ) (<http://www.mimo-db.eu/InstrumentsKeywords/3573>) }
  VALUES (?clarinet) { (<http://data.doremus.org/vocabulary/iaml/mop/wcl>) (<http://www.mimo-db.eu/InstrumentsKeywords/3836>) }
  VALUES (?piano) { (<http://data.doremus.org/vocabulary/iaml/mop/kpf>) (<http://www.mimo-db.eu/InstrumentsKeywords/2299>) }

  FILTER contains(str(?genre), "chambre")
}

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# [en] Give me the works of chamber music that involves at most violin, clarinet and piano
# [fr] Donne-moi les oeuvres de musique de chambre comportant au plus violon, clarinette et piano


SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, ?groupCount AS ?nbInstruments, ?isViolin, ?isClarinet, ?isPiano, ?casting
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting ;
          mus:U12_has_genre / skos:prefLabel ?genre.
  FILTER contains(str(?genre), "chambre") .

  {
    SELECT DISTINCT ?casting ?groupCount ?isViolin ?isClarinet ?isPiano
    WHERE {
      ?casting mus:U23_has_casting_detail ?allCastingDets .
      OPTIONAL {
        ?casting mus:U23_has_casting_detail ?castingDet1 .
        ?castingDet1 mus:U2_foresees_use_of_medium_of_performance ?violin .
        VALUES (?violin) { ( <http://data.doremus.org/vocabulary/iaml/mop/svl> ) (<http://www.mimo-db.eu/InstrumentsKeywords/3573>) }
      }
      BIND(IF(BOUND(?castingDet1), 1, 0) AS ?isViolin )

      OPTIONAL {
        ?casting mus:U23_has_casting_detail ?castingDet2 .
        ?castingDet2 mus:U2_foresees_use_of_medium_of_performance ?clarinet .
        VALUES (?clarinet) { (<http://data.doremus.org/vocabulary/iaml/mop/wcl>) (<http://www.mimo-db.eu/InstrumentsKeywords/3836>) }
      }
      BIND(IF(BOUND(?castingDet2), 1, 0) AS ?isClarinet )

      OPTIONAL {
        ?casting mus:U23_has_casting_detail ?castingDet3 .
        ?castingDet3 mus:U2_foresees_use_of_medium_of_performance ?piano .
        VALUES (?piano) { (<http://data.doremus.org/vocabulary/iaml/mop/kpf>) (<http://www.mimo-db.eu/InstrumentsKeywords/2299>) }
      }
      BIND(IF(BOUND(?castingDet3), 1, 0) AS ?isPiano )

      BIND((?isViolin + ?isClarinet + ?isPiano ) AS ?groupCount)
    }
    GROUP BY ?casting ?groupCount ?isViolin ?isClarinet ?isPiano
    HAVING (?groupCount > 0 AND ?groupCount = count(?allCastingDets))
  }

} ORDER BY desc(?nbInstruments)

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# [en] Give me the works of chamber music that involves at most violin, clarinet and piano, except from the sonatas for violin and piano and clarinet and piano
# [fr] Donne-moi les oeuvres de musique de chambre comportant au plus violon, clarinette et piano, mais pas les sonates pour violon et piano ni pour clarinette et piano


SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, ?groupCount AS ?nbInstruments, ?isViolin, ?isClarinet, ?isPiano, ?casting
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting ;
          mus:U12_has_genre / skos:prefLabel ?genre.
  FILTER contains(str(?genre), "chambre") .

  {
    SELECT DISTINCT ?casting ?groupCount ?isViolin ?isClarinet ?isPiano
    WHERE {
      ?casting mus:U23_has_casting_detail ?allCastingDets .
      OPTIONAL {
        ?casting mus:U23_has_casting_detail ?castingDet1 .
        ?castingDet1 mus:U2_foresees_use_of_medium_of_performance ?violin .
        VALUES (?violin) { ( <http://data.doremus.org/vocabulary/iaml/mop/svl> ) (<http://www.mimo-db.eu/InstrumentsKeywords/3573>) }
      }
      BIND(IF(BOUND(?castingDet1), 1, 0) AS ?isViolin )

      OPTIONAL {
        ?casting mus:U23_has_casting_detail ?castingDet2 .
        ?castingDet2 mus:U2_foresees_use_of_medium_of_performance ?clarinet .
        VALUES (?clarinet) { (<http://data.doremus.org/vocabulary/iaml/mop/wcl>) (<http://www.mimo-db.eu/InstrumentsKeywords/3836>) }
      }
      BIND(IF(BOUND(?castingDet2), 1, 0) AS ?isClarinet )

      OPTIONAL {
        ?casting mus:U23_has_casting_detail ?castingDet3 .
        ?castingDet3 mus:U2_foresees_use_of_medium_of_performance ?piano .
        VALUES (?piano) { (<http://data.doremus.org/vocabulary/iaml/mop/kpf>) (<http://www.mimo-db.eu/InstrumentsKeywords/2299>) }
      }
      BIND(IF(BOUND(?castingDet3), 1, 0) AS ?isPiano )

      BIND((?isViolin + ?isClarinet + ?isPiano ) AS ?groupCount)

    }
    GROUP BY ?casting ?groupCount ?isViolin ?isClarinet ?isPiano
    HAVING (?groupCount > 0 AND ?groupCount = count(?allCastingDets))
  }

  MINUS {
    ?expression a efrbroo:F22_Self-Contained_Expression ;
            mus:U13_has_casting ?casting ;
            mus:U12_has_genre <http://data.doremus.org/vocabulary/iaml/genre/sn> .
    {
      ?casting mus:U23_has_casting_detail ?castingDet1 .
      ?castingDet1 mus:U2_foresees_use_of_medium_of_performance ?violin .
      VALUES (?violin) { ( <http://data.doremus.org/vocabulary/iaml/mop/svl> ) (<http://www.mimo-db.eu/InstrumentsKeywords/3573>) }
    } UNION {
      ?casting mus:U23_has_casting_detail ?castingDet2 .
      ?castingDet2 mus:U2_foresees_use_of_medium_of_performance ?clarinet .
      VALUES (?clarinet) { (<http://data.doremus.org/vocabulary/iaml/mop/wcl>) (<http://www.mimo-db.eu/InstrumentsKeywords/3836>) }
    }

    ?casting mus:U23_has_casting_detail ?castingDet3 .
    ?castingDet3 mus:U2_foresees_use_of_medium_of_performance ?piano .
    VALUES (?piano) { (<http://data.doremus.org/vocabulary/iaml/mop/kpf>) (<http://www.mimo-db.eu/InstrumentsKeywords/2299>) }
  }
} ORDER BY desc(?nbInstruments)

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# [en] Give me all the melodies written on French texts for average voice between 1870 and 1913.
# [fr] Donne-moi toutes les mélodies écrites sur des textes français pour voix moyenne entre 1870 et 1913.


SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, ?voix
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting ;
          mus:U12_has_genre <http://data.doremus.org/vocabulary/iaml/genre/mld> .

  ?casting mus:U23_has_casting_detail / mus:U2_foresees_use_of_medium_of_performance ?avgVoice .
  ?avgVoice skos:prefLabel ?voix.

  ?expCreation efrbroo:R17_created ?expression ;
          ecrm:P4_has_time-span ?ts.
  ?ts time:hasEnd / time:inXSDDate ?end ;
                time:hasBeginning / time:inXSDDate ?start .

  FILTER ( ?start >= "1870"^^xsd:gYear AND ?end <= "1913"^^xsd:gYear AND lang(?voix) = "en" )

  VALUES ?avgVoice { <http://data.doremus.org/vocabulary/iaml/mop/vbr> <http://data.doremus.org/vocabulary/iaml/mop/vms> }

} GROUP BY ?expression ?voix

# Note: we have not the information on the language of the text

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# [en] Give me all the slow movements of chamber music that foresee at least one cello
# [fr] Donne-moi tous les mouvements lents d’oeuvres de musique de chambre dans lesquelles figure au moins un violoncelle

SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, SAMPLE(?genre) AS ?genre
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting ;
          mus:U12_has_genre / skos:prefLabel ?genre.
  FILTER contains(str(?genre), "chambre") .

  ?work a efrbroo:F14_Individual_Work;
        efrbroo:R9_is_realised_in ?expression .
  OPTIONAL {
    ?work ecrm:P148_has_component ?movement .
    #  ?movement mus:U14_has_tempo ?tempo .
  }

  ?casting mus:U23_has_casting_detail ?castingDet .
  ?castingDet mus:U2_foresees_use_of_medium_of_performance ?cello .

  VALUES (?cello) { (<http://data.doremus.org/vocabulary/iaml/mop/svc>) (<http://www.mimo-db.eu/InstrumentsKeywords/3582>) }

} GROUP BY ?expression

# Note: we have not the information about the movement and about the tempos

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# [en] Give me all the sacred vocal music for choir written in England after 1945
# [fr] Donne-moi toute la musique vocale sacrée pour choeur écrite en Angleterre depuis 1945


SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, ?end as ?composed, ?place, SAMPLE(?genre) AS ?genre
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting / mus:U23_has_casting_detail ?castingDet ;
          mus:U12_has_genre / skos:prefLabel ?genre.
  FILTER regex(str(?genre), "sacr(a|è|ed)") .

  {
    ?castingDet mus:U2_foresees_use_of_medium_of_performance <http://data.doremus.org/vocabulary/iaml/mop/c> .
  } UNION {
    ?castingDet mus:U2_foresees_use_of_medium_of_performance / skos:broader <http://data.doremus.org/vocabulary/iaml/mop/c> .
  }

  ?expCreation efrbroo:R17_created ?expression ;
          ecrm:P4_has_time-span / time:hasEnd / time:inXSDDate ?end .

  OPTIONAL { ?expCreation ecrm:P7_took_place_at ?place }

  FILTER ( ?end > "1945"^^xsd:gYear )

} ORDER BY ?end

# Note: we have not the information about the composition place

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
PREFIX efrbroo: <http://erlangen-crm.org/efrbroo/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# [en] Give me all the operas of which the composer is also the librettist
# [fr] Donne-moi tous les opéras dont le compositeur est également le librettiste

SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, SAMPLE(?composerName) as ?composer
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U12_has_genre <http://data.doremus.org/vocabulary/iaml/genre/op> .

  ?expCreation efrbroo:R17_created ?expression ;
          ecrm:P9_consists_of [ ecrm:P14_carried_out_by ?composer  ;
                                 mus:U31_had_function <http://data.doremus.org/vocabulary/function/composer>
                               ] ,
                               [ ecrm:P14_carried_out_by ?composer  ;
                                    mus:U31_had_function <http://data.doremus.org/vocabulary/function/librettist>
                               ] .

  ?composer foaf:name ?composerName .
} LIMIT 500

PREFIX mus: <http://data.doremus.org/ontology#>
PREFIX ecrm: <http://erlangen-crm.org/current/>
