  ?casting mus:U23_has_casting_detail ?castingDet2 .
  ?castingDet2 mus:U2_foresees_use_of_medium_of_performance ?clarinet.

  ?casting mus:U23_has_casting_detail ?castingDet3 .
  ?castingDet3 mus:U2_foresees_use_of_medium_of_performance  ?piano .

  VALUES (?violin) { ( <http://data.doremus.org/vocabulary/iaml/mop/svl> ) (<http://www.mimo-db.eu/InstrumentsKeywords/3573>) }
  VALUES (?clarinet) { (<http://data.doremus.org/vocabulary/iaml/mop/wcl>) (<http://www.mimo-db.eu/InstrumentsKeywords/3836>) }
  VALUES (?piano) { (<http://data.doremus.org/vocabulary/iaml/mop/kpf>) (<http://www.mimo-db.eu/InstrumentsKeywords/2299>) }

  FILTER contains(str(?genre), "chambre")
}

SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, ?groupCount AS ?nbInstruments, ?isViolin, ?isClarinet, ?isPiano, ?casting
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting ;
          mus:U12_has_genre / skos:prefLabel ?genre.
  FILTER contains(str(?genre), "chambre") .

  {
    SELECT DISTINCT ?casting ?groupCount ?isViolin ?isClarinet ?isPiano
    WHERE {
      ?casting mus:U23_has_casting_detail ?allCastingDets .
      OPTIONAL {
        ?casting mus:U23_has_casting_detail ?castingDet1 .
        ?castingDet1 mus:U2_foresees_use_of_medium_of_performance ?violin .
        VALUES (?violin) { ( <http://data.doremus.org/vocabulary/iaml/mop/svl> ) (<http://www.mimo-db.eu/InstrumentsKeywords/3573>) }
      }
      BIND(IF(BOUND(?castingDet1), 1, 0) AS ?isViolin )

      OPTIONAL {
        ?casting mus:U23_has_casting_detail ?castingDet2 .
        ?castingDet2 mus:U2_foresees_use_of_medium_of_performance ?clarinet .
        VALUES (?clarinet) { (<http://data.doremus.org/vocabulary/iaml/mop/wcl>) (<http://www.mimo-db.eu/InstrumentsKeywords/3836>) }
      }
      BIND(IF(BOUND(?castingDet2), 1, 0) AS ?isClarinet )

      OPTIONAL {
        ?casting mus:U23_has_casting_detail ?castingDet3 .
        ?castingDet3 mus:U2_foresees_use_of_medium_of_performance ?piano .
        VALUES (?piano) { (<http://data.doremus.org/vocabulary/iaml/mop/kpf>) (<http://www.mimo-db.eu/InstrumentsKeywords/2299>) }
      }
      BIND(IF(BOUND(?castingDet3), 1, 0) AS ?isPiano )

      BIND((?isViolin + ?isClarinet + ?isPiano ) AS ?groupCount)
    }
    GROUP BY ?casting ?groupCount ?isViolin ?isClarinet ?isPiano
    HAVING (?groupCount > 0 AND ?groupCount = count(?allCastingDets))
  }

} ORDER BY desc(?nbInstruments)

SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, ?groupCount AS ?nbInstruments, ?isViolin, ?isClarinet, ?isPiano, ?casting
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting ;
          mus:U12_has_genre / skos:prefLabel ?genre.
  FILTER contains(str(?genre), "chambre") .

  {
    SELECT DISTINCT ?casting ?groupCount ?isViolin ?isClarinet ?isPiano
    WHERE {
      ?casting mus:U23_has_casting_detail ?allCastingDets .
      OPTIONAL {
        ?casting mus:U23_has_casting_detail ?castingDet1 .
        ?castingDet1 mus:U2_foresees_use_of_medium_of_performance ?violin .
        VALUES (?violin) { ( <http://data.doremus.org/vocabulary/iaml/mop/svl> ) (<http://www.mimo-db.eu/InstrumentsKeywords/3573>) }
      }
      BIND(IF(BOUND(?castingDet1), 1, 0) AS ?isViolin )

      OPTIONAL {
        ?casting mus:U23_has_casting_detail ?castingDet2 .
        ?castingDet2 mus:U2_foresees_use_of_medium_of_performance ?clarinet .
        VALUES (?clarinet) { (<http://data.doremus.org/vocabulary/iaml/mop/wcl>) (<http://www.mimo-db.eu/InstrumentsKeywords/3836>) }
      }
      BIND(IF(BOUND(?castingDet2), 1, 0) AS ?isClarinet )

      OPTIONAL {
        ?casting mus:U23_has_casting_detail ?castingDet3 .
        ?castingDet3 mus:U2_foresees_use_of_medium_of_performance ?piano .
        VALUES (?piano) { (<http://data.doremus.org/vocabulary/iaml/mop/kpf>) (<http://www.mimo-db.eu/InstrumentsKeywords/2299>) }
      }
      BIND(IF(BOUND(?castingDet3), 1, 0) AS ?isPiano )

      BIND((?isViolin + ?isClarinet + ?isPiano ) AS ?groupCount)

    }
    GROUP BY ?casting ?groupCount ?isViolin ?isClarinet ?isPiano
    HAVING (?groupCount > 0 AND ?groupCount = count(?allCastingDets))
  }

  MINUS {
    ?expression a efrbroo:F22_Self-Contained_Expression ;
            mus:U13_has_casting ?casting ;
            mus:U12_has_genre <http://data.doremus.org/vocabulary/iaml/genre/sn> .
    {
      ?casting mus:U23_has_casting_detail ?castingDet1 .
      ?castingDet1 mus:U2_foresees_use_of_medium_of_performance ?violin .
      VALUES (?violin) { ( <http://data.doremus.org/vocabulary/iaml/mop/svl> ) (<http://www.mimo-db.eu/InstrumentsKeywords/3573>) }
    } UNION {
      ?casting mus:U23_has_casting_detail ?castingDet2 .
      ?castingDet2 mus:U2_foresees_use_of_medium_of_performance ?clarinet .
      VALUES (?clarinet) { (<http://data.doremus.org/vocabulary/iaml/mop/wcl>) (<http://www.mimo-db.eu/InstrumentsKeywords/3836>) }
    }

    ?casting mus:U23_has_casting_detail ?castingDet3 .
    ?castingDet3 mus:U2_foresees_use_of_medium_of_performance ?piano .
    VALUES (?piano) { (<http://data.doremus.org/vocabulary/iaml/mop/kpf>) (<http://www.mimo-db.eu/InstrumentsKeywords/2299>) }
  }
} ORDER BY desc(?nbInstruments)

SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, ?voix
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting ;
          mus:U12_has_genre <http://data.doremus.org/vocabulary/iaml/genre/mld> .

  ?casting mus:U23_has_casting_detail / mus:U2_foresees_use_of_medium_of_performance ?avgVoice .
  ?avgVoice skos:prefLabel ?voix.

  ?expCreation efrbroo:R17_created ?expression ;
          ecrm:P4_has_time-span ?ts.
  ?ts time:hasEnd / time:inXSDDate ?end ;
                time:hasBeginning / time:inXSDDate ?start .

  FILTER ( ?start >= "1870"^^xsd:gYear AND ?end <= "1913"^^xsd:gYear AND lang(?voix) = "en" )

  VALUES ?avgVoice { <http://data.doremus.org/vocabulary/iaml/mop/vbr> <http://data.doremus.org/vocabulary/iaml/mop/vms> }

} GROUP BY ?expression ?voix

# Note: we have not the information on the language of the text

SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, SAMPLE(?genre) AS ?genre
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting ;
          mus:U12_has_genre / skos:prefLabel ?genre.
  FILTER contains(str(?genre), "chambre") .

  ?work a efrbroo:F14_Individual_Work;
        efrbroo:R9_is_realised_in ?expression .
  OPTIONAL {
    ?work ecrm:P148_has_component ?movement .
    #  ?movement mus:U14_has_tempo ?tempo .
  }

  ?casting mus:U23_has_casting_detail ?castingDet .
  ?castingDet mus:U2_foresees_use_of_medium_of_performance ?cello .

  VALUES (?cello) { (<http://data.doremus.org/vocabulary/iaml/mop/svc>) (<http://www.mimo-db.eu/InstrumentsKeywords/3582>) }

} GROUP BY ?expression

# Note: we have not the information about the movement and about the tempos

SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, ?end as ?composed, ?place, SAMPLE(?genre) AS ?genre
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting / mus:U23_has_casting_detail ?castingDet ;
          mus:U12_has_genre / skos:prefLabel ?genre.
  FILTER regex(str(?genre), "sacr(a|è|ed)") .

  {
    ?castingDet mus:U2_foresees_use_of_medium_of_performance <http://data.doremus.org/vocabulary/iaml/mop/c> .
  } UNION {
    ?castingDet mus:U2_foresees_use_of_medium_of_performance / skos:broader <http://data.doremus.org/vocabulary/iaml/mop/c> .
  }

  ?expCreation efrbroo:R17_created ?expression ;
          ecrm:P4_has_time-span / time:hasEnd / time:inXSDDate ?end .

  OPTIONAL { ?expCreation ecrm:P7_took_place_at ?place }

  FILTER ( ?end > "1945"^^xsd:gYear )

} ORDER BY ?end

# Note: we have not the information about the composition place

