SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, ?compositionDate
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title .
  ?expCreation efrbroo:R17_created ?expression ;
          ecrm:P9_consists_of / ecrm:P14_carried_out_by ?composer ;
          ecrm:P4_has_time-span / time:hasBeginning / time:inXSDDate ?compositionDate .

  ?composer foaf:name "Wolfgang Amadeus Mozart" ;
          schema:deathDate ?death .

  FILTER ( year(?compositionDate) - year(?death) <= 5 )
} ORDER BY ?compositionDate

SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, SAMPLE(?authorName) as ?author
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U12_has_genre <http://data.doremus.org/vocabulary/iaml/genre/mld> ;
          efrbroo:R5_has_component ?single .

  [] efrbroo:R17_created ?single ;
          ecrm:P9_consists_of  [ ecrm:P14_carried_out_by ?author ;
                                    mus:U31_had_function <http://data.doremus.org/vocabulary/function/author> ] .
  ?author foaf:name ?authorName .

}
GROUP BY ?expression
HAVING (COUNT(DISTINCT ?author) = 1)
LIMIT 100

SELECT DISTINCT ?expression, SAMPLE(?title) as ?title, ?start as ?compositionDate, ?nbInstruments, ?casting
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          rdfs:label ?title ;
          mus:U13_has_casting ?casting .
  {
    SELECT ?casting, SUM(?q) AS ?nbInstruments
    WHERE {
      VALUES (?piano) { (<http://data.doremus.org/vocabulary/iaml/mop/kpf>) (<http://www.mimo-db.eu/InstrumentsKeywords/2299>) }
      VALUES (?ensambles) { (<http://data.doremus.org/vocabulary/iaml/mop/o>) (<http://data.doremus.org/vocabulary/iaml/mop/c>) }

      ?casting mus:U23_has_casting_detail / mus:U2_foresees_use_of_medium_of_performance ?piano .

      ?casting mus:U23_has_casting_detail ?castingDet .
      OPTIONAL { ?castingDet mus:U30_foresees_quantity_of_mop ?qty }
      BIND(coalesce(?qty, 1) AS ?q)

      MINUS { ?casting mus:U23_has_casting_detail / mus:U2_foresees_use_of_medium_of_performance / skos:broader ?ensambles }
    } GROUP BY ?casting
    HAVING (SUM(?qty) <= 6 AND SUM(?qty) > 1)
  }

  ?expCreation efrbroo:R17_created ?expression ;
          ecrm:P4_has_time-span ?ts.
  ?ts time:hasEnd / time:inXSDDate ?end ;
                time:hasBeginning / time:inXSDDate ?start .

  FILTER ( ?start >= "1860"^^xsd:gYear AND ?end <= "1880"^^xsd:gYear  )
}
ORDER BY ?start
LIMIT 500

SELECT DISTINCT ?expression SAMPLE(?title) as ?title ?catNum
WHERE {
  ?expression a efrbroo:F22_Self-Contained_Expression ;
          mus:U16_has_catalogue_statement ?catalogue ;
          rdfs:label ?title .
  ?expCreation efrbroo:R17_created ?expression ;
          ecrm:P9_consists_of / ecrm:P14_carried_out_by ?composer .
  ?composer foaf:name "Johann Sebastian Bach" .
  ?catalogue mus:U40_has_catalogue_name / modsrdf:identifier "BWV";
            mus:U41_has_catalogue_number ?catNum .
  FILTER (?catNum >= 30 and ?catNum <= 70 )
} ORDER BY ?catNum

SELECT DISTINCT ?work, SAMPLE(?title) as ?title, ?link, ?work2, SAMPLE(?title2) as ?title2
WHERE {
  ?work ?link ?work2.

  ?work a efrbroo:F14_Individual_Work;
        efrbroo:R9_is_realised_in ?expression .

  ?expression rdfs:label ?title ;
        mus:U13_has_casting ?casting .

  { SELECT ?casting
    WHERE {
    ?casting mus:U23_has_casting_detail / mus:U2_foresees_use_of_medium_of_performance / skos:exactMatch* <http://data.doremus.org/vocabulary/iaml/mop/kpf> ;
          mus:U23_has_casting_detail ?cDet .
    } GROUP BY ?casting
    HAVING (COUNT(?cDet) = 1)
  }

